import ExpenseItem from "./components/ExpenseItem";
import Card from "./components/Card";
import NewExpense from "./components/NewExpense";
import React, {useState} from 'react';


function App() {

  const dummyExpenses = [
    {title: 'Car Insurance', amount: 280, date: new Date(2021, 3, 28)},
    {title: 'Dog Food', amount: 39, date: new Date(2021, 5, 12)},
    {title: 'Netflix', amount: 20, date: new Date(2021, 1, 30)},
    {title: 'Udemy', amount: 68, date: new Date(2021, 8, 24)},
  ];

  const addExpenseHandler = (expense) => {
    const expenseData = {
      ...expense,
    };
    dummyExpenses.push(expenseData);
  };

  return (
    <Card>
      <NewExpense onAddExpense={addExpenseHandler}/>

      {dummyExpenses.map(expense => {
        return <ExpenseItem title={expense.title} amount={expense.amount} date={expense.date} />
      })};

    </Card>
  );
}

export default App;
