import ExpenseItem from "./components/ExpenseItem";
import Card from "./components/Card";
import NewExpense from "./components/NewExpense";

import React, {useState} from 'react';


const DUMMY = [
  {title: 'Car Insurance', amount: 280, date: new Date(2021, 3, 28)},
  {title: 'Dog Food', amount: 39, date: new Date(2021, 5, 12)},
  {title: 'Netflix', amount: 20, date: new Date(2021, 1, 30)},
  {title: 'Udemy', amount: 12, date: new Date(2021, 8, 24)},
];

function App() {

  const [expenses, setExpense] = useState(DUMMY)

  const addExpenseHandler = (expense) => {
    const expenseData = {
      ...expense,
    };
    console.log(expenseData);
  };

  return (
    <Card>
      <NewExpense onAddExpense={addExpenseHandler}/>

      {DUMMY.map(expense => {
        return <ExpenseItem title={expense.title} amount={expense.amount} date={expense.date}></ExpenseItem>
      })};

    </Card>
  );
}

export default App;
